    public static void main(String[] args)
    {
        ArrayList<Integer> rle = new ArrayList<>();
        int counter = 0;
        int previous = 0;
        int zeroCounter = 0;
        int oneCounter = 0;
        int longest = 0;
        for (int i = 0; i < 100; i++)
        {
            for (int j = 0; j < 100; j++)
            {
                int currentValue = pattern[j + i * 100];
                if (currentValue == 1) {
                    System.out.print("■");
                    oneCounter++;
                }else {
                    System.out.print("□");
                    zeroCounter++;
                }

                if (currentValue == previous){
                    counter++;
                }else{
                    rle.add(counter);
                    if (counter > longest){
                        longest = counter;
                    }
                    counter = 1;
                    previous = currentValue;
                }
            }
            rle.add(counter);
            System.out.println();
        }

        System.out.println("Pattern length before compression " + pattern.length + " bits");
        System.out.println(rle);

        System.out.println("Number of zeroes: " + zeroCounter);
        System.out.println("Number of ones: " + oneCounter);
        System.out.println("Longest uninterrupted string of repeats: " + longest);
    }
